function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),n=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),n.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),n.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"),n.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function numCounter(e,t,n,s){const c=document.querySelector(e);let r=0;const o=Math.round(n/(t/s));let i=setInterval((()=>{r+=s,r===t&&clearInterval(i),c.innerHTML=r}),o)}burgerMenu(),window.addEventListener("scroll",fixedNav),numCounter("#num1",3800,2500,100),numCounter("#num2",230,2500,10),numCounter("#num3",500,2500,10);